- (NSString *)gt_stringByReplacingEmojiCheatCodesWithUnicode
{
    if (!gt_s_cheatCodesToUnicode) {
        [NSString gt_initializeEmojiCheatCodes];
    }

    if ([self rangeOfString:@":"].location != NSNotFound) {
        __block NSMutableString *newText = [NSMutableString stringWithString:self];
        [gt_s_cheatCodesToUnicode enumerateKeysAndObjectsUsingBlock:^(NSString *key, id obj, BOOL *stop) {
            [newText replaceOccurrencesOfString:key withString:obj options:NSLiteralSearch range:NSMakeRange(0, newText.length)];
        }];
        return newText;
    }

    return self;
}

- (NSString *)gt_stringByReplacingEmojiUnicodeWithCheatCodes
{
    if (!gt_s_cheatCodesToUnicode) {
        [NSString gt_initializeEmojiCheatCodes];
    }

    if (self.length) {
        __block NSMutableString *newText = [NSMutableString stringWithString:self];
        [gt_s_unicodeToCheatCodes enumerateKeysAndObjectsUsingBlock:^(id key, id obj, BOOL *stop) {
            NSString *string = ([obj isKindOfClass:[NSArray class]] ? [obj firstObject] : obj);
            [newText replaceOccurrencesOfString:key withString:string options:NSLiteralSearch range:NSMakeRange(0, newText.length)];
        }];
        return newText;
    }
    return self;
}


- (BOOL)gt_containsEmoji{
    //    return  [self rangeOfCharacterFromSet:[NSCharacterSet emojiCharacterSet]].location != NSNotFound;

    if ([self gt_isFuckEmoji]) {
        return YES;
    }
    const unichar high = [self characterAtIndex:0];


    // Surrogate pair (U+1D000-1F77F)
    if (0xd800 <= high && high <= 0xdbff) {
        const unichar low = [self characterAtIndex: 1];
        const int codepoint = ((high - 0xd800) * 0x400) + (low - 0xdc00) + 0x10000;

        return (0x1d000 <= codepoint && codepoint <= 0x1f77f);

        // Not surrogate pair (U+2100-27BF)
    } else {
        return (0x2100 <= high && high <= 0x27bf);
    }
