- (NSString *)gt_stringByRemovingEmoji {
    return  [self gt_removingEmoji];
}

- (NSString *)gt_removingEmoji {
    return  [self gt_stringByReplaceingEmojiWithString:@""];
}
- (NSString *)gt_stringByReplaceingEmojiWithString:(NSString*)string{
    NSMutableString* __block buffer = [NSMutableString stringWithCapacity:[self length]];

    [self enumerateSubstringsInRange:NSMakeRange(0, [self length])
                             options:NSStringEnumerationByComposedCharacterSequences
                          usingBlock: ^(NSString* substring, NSRange substringRange, NSRange enclosingRange, BOOL* stop) {
                              [buffer appendString:([substring gt_containsEmoji])?(string?:@""): substring];
                          }];

    return buffer;
}
- (NSString *)gt_allEmojiString{
    NSMutableString* __block buffer = [NSMutableString stringWithCapacity:[self length]];
    [self enumerateSubstringsInRange:NSMakeRange(0, [self length])
                             options:NSStringEnumerationByComposedCharacterSequences
                          usingBlock: ^(NSString* substring, NSRange substringRange, NSRange enclosingRange, BOOL* stop) {
                              if ([substring gt_containsEmoji]) {
                                  [buffer appendString:substring];
                              }
                          }];
    return buffer;
}
- (NSArray<NSString *>*)gt_allEmoji{
    __block NSMutableArray *allEmoji = [NSMutableArray array];
    [self enumerateSubstringsInRange:NSMakeRange(0, [self length])
                             options:NSStringEnumerationByComposedCharacterSequences
                          usingBlock: ^(NSString* substring, NSRange substringRange, NSRange enclosingRange, BOOL* stop) {
                              if ([substring gt_containsEmoji]) {
                                  [allEmoji addObject:substring];
                              }
                          }];
    return allEmoji;
}
- (NSArray<NSString *>*)gt_allEmojiRanges{
    __block NSMutableArray *rangs = [NSMutableArray array];
    [self enumerateSubstringsInRange:NSMakeRange(0, [self length])
                             options:NSStringEnumerationByComposedCharacterSequences
                          usingBlock: ^(NSString * substring, NSRange substringRange, NSRange enclosingRange, BOOL* stop) {
                              if ([substring gt_containsEmoji]) {
                                  [rangs addObject:NSStringFromRange(substringRange)];
                              }
                          }];
    return rangs;
}


